sourcesContent:
import React, { useState, useContext } from 'react';
import { 
  Box, Typography, Grid, Card, CardContent, Button, Chip, TextField, 
  Dialog, DialogTitle, DialogContent, DialogActions, MenuItem, List, ListItem, 
  ListItemText, IconButton, Paper, Tabs, Tab, Divider, Avatar, Stack
} from '@mui/material';
import ScheduleIcon from '@mui/icons-material/Schedule';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import AssignmentIcon from '@mui/icons-material/Assignment';
import DownloadIcon from '@mui/icons-material/Download';
import UploadFileIcon from '@mui/icons-material/UploadFile';
import PictureAsPdfIcon from '@mui/icons-material/PictureAsPdf';
import DescriptionIcon from '@mui/icons-material/Description';
import ImageIcon from '@mui/icons-material/Image';
import FolderZipIcon from '@mui/icons-material/FolderZip';
import SlideshowIcon from '@mui/icons-material/Slideshow';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import PendingIcon from '@mui/icons-material/Pending';
import ErrorIcon from '@mui/icons-material/Error';
import { UserContext } from '../App';

// Mock data
const timetableData = {
  'A': [
    { day: 'Monday', periods: [
      { time: '09:00 AM', subject: 'Data Structures', faculty: 'Prof. Amit Verma', room: 'Room 301' },
      { time: '11:00 AM', subject: 'Database Systems', faculty: 'Dr. Sneha Patel', room: 'Lab 2' },
      { time: '02:00 PM', subject: 'Machine Learning', faculty: 'Prof. Amit Verma', room: 'Room 405' }
    ]},
    { day: 'Tuesday', periods: [
      { time: '09:00 AM', subject: 'Web Technologies', faculty: 'Dr. Sneha Patel', room: 'Room 302' },
      { time: '11:00 AM', subject: 'Operating Systems', faculty: 'Prof. Amit Verma', room: 'Room 301' }
    ]},
    { day: 'Wednesday', periods: [
      { time: '09:00 AM', subject: 'Data Structures', faculty: 'Prof. Amit Verma', room: 'Room 301' },
      { time: '02:00 PM', subject: 'Database Systems', faculty: 'Dr. Sneha Patel', room: 'Lab 2' }
    ]},
    { day: 'Thursday', periods: [
      { time: '11:00 AM', subject: 'Machine Learning', faculty: 'Prof. Amit Verma', room: 'Room 405' },
      { time: '02:00 PM', subject: 'Web Technologies', faculty: 'Dr. Sneha Patel', room: 'Lab 3' }
    ]},
    { day: 'Friday', periods: [
      { time: '09:00 AM', subject: 'Operating Systems', faculty: 'Prof. Amit Verma', room: 'Room 301' },
      { time: '11:00 AM', subject: 'Project Work', faculty: 'Dr. Sneha Patel', room: 'Lab 1' }
    ]}
  ],
  'B': [
    { day: 'Monday', periods: [
      { time: '10:00 AM', subject: 'Data Structures', faculty: 'Prof. Amit Verma', room: 'Room 302' },
      { time: '01:00 PM', subject: 'Database Systems', faculty: 'Dr. Sneha Patel', room: 'Lab 3' }
    ]},
    { day: 'Tuesday', periods: [
      { time: '10:00 AM', subject: 'Machine Learning', faculty: 'Prof. Amit Verma', room: 'Room 405' }
    ]}
  ]
};

const studyMaterials = [
  {
    subject: 'Data Structures',
    subjectCode: 'CS301',
    materials: [
      { name: 'Binary Trees Lecture.pptx', size: '2.4 MB', type: 'pptx' },
      { name: 'Graph Algorithms.pdf', size: '1.8 MB', type: 'pdf' },
      { name: 'DSA Notes.docx', size: '890 KB', type: 'docx' }
    ]
  },
  {
    subject: 'Database Systems',
    subjectCode: 'CS401',
    materials: [
      { name: 'SQL Queries Tutorial.pdf', size: '3.2 MB', type: 'pdf' },
      { name: 'ER Diagrams.png', size: '450 KB', type: 'png' },
      { name: 'Database Design.ppt', size: '5.1 MB', type: 'ppt' }
    ]
  },
  {
    subject: 'Machine Learning',
    subjectCode: 'CS501',
    materials: [
      { name: 'Neural Networks.pdf', size: '4.5 MB', type: 'pdf' },
      { name: 'ML Algorithms.zip', size: '12.3 MB', type: 'zip' },
      { name: 'Regression Models.pptx', size: '2.7 MB', type: 'pptx' }
    ]
  }
];

const assignments = [
  {
    id: 1,
    subject: 'Data Structures',
    subjectCode: 'CS301',
    topic: 'Binary Search Tree Implementation',
    description: 'Implement BST with insert, delete, and search operations',
    dueDate: '2025-12-20',
    status: 'pending'
  },
  {
    id: 2,
    subject: 'Database Systems',
    subjectCode: 'CS401',
    topic: 'SQL Query Assignment',
    description: 'Write complex SQL queries for the given database schema',
    dueDate: '2025-12-18',
    status: 'pending'
  },
  {
    id: 3,
    subject: 'Machine Learning',
    subjectCode: 'CS501',
    topic: 'Linear Regression Project',
    description: 'Build and train a linear regression model on the provided dataset',
    dueDate: '2025-12-05',
    status: 'overdue'
  },
  {
    id: 4,
    subject: 'Web Technologies',
    subjectCode: 'CS402',
    topic: 'React Component Design',
    description: 'Create reusable React components for a dashboard',
    dueDate: '2025-12-15',
    status: 'submitted'
  }
];

const getFileIcon = (type) => {
  switch(type) {
    case 'pdf': return <PictureAsPdfIcon sx={{ color: '#d32f2f' }} />;
    case 'doc':
    case 'docx': return <DescriptionIcon sx={{ color: '#1976d2' }} />;
    case 'ppt':
    case 'pptx': return <SlideshowIcon sx={{ color: '#ed6c02' }} />;
    case 'jpg':
    case 'png':
    case 'jpeg': return <ImageIcon sx={{ color: '#2e7d32' }} />;
    case 'zip': return <FolderZipIcon sx={{ color: '#9c27b0' }} />;
    default: return <DescriptionIcon />;
  }
};

const getStatusChip = (status) => {
  switch(status) {
    case 'submitted':
      return <Chip icon={<CheckCircleIcon />} label="Submitted" color="success" size="small" />;
    case 'pending':
      return <Chip icon={<PendingIcon />} label="Pending" color="warning" size="small" />;
    case 'overdue':
      return <Chip icon={<ErrorIcon />} label="Overdue" color="error" size="small" />;
    default:
      return <Chip label={status} size="small" />;
  }
};

const Academics = () => {
  const { user } = useContext(UserContext);
  const [activeTab, setActiveTab] = useState(0);
  const [selectedSection, setSelectedSection] = useState('A');
  const [submitDialogOpen, setSubmitDialogOpen] = useState(false);
  const [selectedAssignment, setSelectedAssignment] = useState(null);
  const [uploadFile, setUploadFile] = useState(null);
  
  // Database state
  const [assignments, setAssignments] = useState([]);
  const [resources, setResources] = useState([]);
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch data from database
  React.useEffect(() => {
    const token = localStorage.getItem('token');
    
    Promise.all([
      fetch('/api/assignments', {
        headers: { 'Authorization': `Bearer ${token}` }
      }).then(res => res.ok ? res.json() : []),
      
      fetch('/api/resources', {
        headers: { 'Authorization': `Bearer ${token}` }
      }).then(res => res.ok ? res.json() : []),
      
      fetch('/api/submissions/my', {
        headers: { 'Authorization': `Bearer ${token}` }
      }).then(res => res.ok ? res.json() : [])
    ])
    .then(([assignmentsData, resourcesData, submissionsData]) => {
      setAssignments(assignmentsData);
      setResources(resourcesData);
      setSubmissions(submissionsData);
      setLoading(false);
    })
    .catch(err => {
      console.error('Error fetching academics data:', err);
      setLoading(false);
    });
  }, []);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const handleSubmitAssignment = (assignment) => {
    setSelectedAssignment(assignment);
    setSubmitDialogOpen(true);
  };

  const handleFileUpload = (event) => {
    setUploadFile(event.target.files[0]);
  };

  const handleDownloadTimetable = () => {
    alert('Downloading timetable as PDF...');
  };

  const handleDownloadAllMaterials = () => {
    alert('Downloading all study materials...');
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" fontWeight={700} mb={1}>
        Academics
      </Typography>
      <Typography variant="body2" color="textSecondary" mb={3}>
        Your classes, materials, and assignments
      </Typography>

      {/* Tabs */}
      <Tabs 
        value={activeTab} 
        onChange={handleTabChange}
        sx={{ 
          mb: 3,
          '& .MuiTab-root': {
            textTransform: 'none',
            fontWeight: 600,
            fontSize: '0.95rem'
          }
        }}
      >
        <Tab label="Timetable" />
        <Tab label="Materials" />
        <Tab label="Assignments" />
      </Tabs>

      {/* Timetable Section */}
      {activeTab === 0 && (
        <Box>
          <Card sx={{ mb: 3, boxShadow: '0 2px 8px rgba(0,0,0,0.08)', borderRadius: 2 }}>
            <CardContent>
              <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
                <Box display="flex" alignItems="center" gap={1}>
                  <ScheduleIcon sx={{ color: '#667eea', fontSize: 28 }} />
                  <Typography variant="h6" fontWeight={600}>
                    Class Timetable
                  </Typography>
                </Box>
                <TextField
                  select
                  size="small"
                  value={selectedSection}
                  onChange={(e) => setSelectedSection(e.target.value)}
                  sx={{ minWidth: 120 }}
                  label="Section"
                >
                  <MenuItem value="A">Section A</MenuItem>
                  <MenuItem value="B">Section B</MenuItem>
                  <MenuItem value="C">Section C</MenuItem>
                </TextField>
              </Box>

              {/* Today's Schedule Highlight */}
              <Paper sx={{ p: 2, mb: 3, bgcolor: '#f8f9ff', border: '1px solid #e3e8ff' }}>
                <Typography variant="subtitle2" fontWeight={600} color="primary" mb={2}>
                  Today's Schedule
                </Typography>
                {timetableData[selectedSection]?.[0]?.periods.map((period, idx) => (
                  <Box key={idx} display="flex" alignItems="center" gap={2} mb={1.5}>
                    <Box sx={{ width: 4, height: 40, bgcolor: '#667eea', borderRadius: 1 }} />
                    <Box flex={1}>
                      <Typography variant="body2" fontWeight={600}>
                        {period.subject}
                      </Typography>
                      <Typography variant="caption" color="textSecondary">
                        {period.faculty}  {period.room}
                      </Typography>
                    </Box>
                    <Chip 
                      label={period.time} 
                      size="small" 
                      sx={{ 
                        bgcolor: 'white',
                        fontWeight: 500,
                        fontSize: '0.75rem'
                      }} 
                    />
                  </Box>
                ))}
              </Paper>

              {/* Weekly Timetable */}
              <Box sx={{ overflowX: 'auto' }}>
                {timetableData[selectedSection]?.map((daySchedule, dayIdx) => (
                  <Box key={dayIdx} mb={2}>
                    <Typography variant="subtitle2" fontWeight={600} mb={1} color="textSecondary">
                      {daySchedule.day}
                    </Typography>
                    <Grid container spacing={2}>
                      {daySchedule.periods.map((period, periodIdx) => (
                        <Grid item xs={12} md={4} key={periodIdx}>
                          <Card 
                            sx={{ 
                              p: 2, 
                              borderRadius: 2,
                              border: '1px solid #e0e0e0',
                              transition: 'all 0.2s',
                              '&:hover': {
                                boxShadow: '0 4px 12px rgba(102,126,234,0.15)',
                                borderColor: '#667eea'
                              }
                            }}
                          >
                            <Typography variant="body2" fontWeight={600} mb={0.5}>
                              {period.subject}
                            </Typography>
                            <Typography variant="caption" color="textSecondary" display="block" mb={0.5}>
                              {period.faculty}
                            </Typography>
                            <Box display="flex" justifyContent="space-between" alignItems="center" mt={1}>
                              <Chip 
                                label={period.time} 
                                size="small" 
                                sx={{ fontSize: '0.7rem', height: 22 }}
                              />
                              <Typography variant="caption" color="primary" fontWeight={500}>
                                {period.room}
                              </Typography>
                            </Box>
                          </Card>
                        </Grid>
                      ))}
                    </Grid>
                  </Box>
                ))}
              </Box>

              <Button 
                variant="contained" 
                startIcon={<DownloadIcon />}
                onClick={handleDownloadTimetable}
                sx={{ 
                  mt: 2,
                  textTransform: 'none',
                  bgcolor: '#667eea',
                  '&:hover': { bgcolor: '#5568d3' }
                }}
              >
                Download Timetable (PDF)
              </Button>
            </CardContent>
          </Card>
        </Box>
      )}

      {/* Materials Section */}
      {activeTab === 1 && (
        <Box>
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
            <Box display="flex" alignItems="center" gap={1}>
              <MenuBookIcon sx={{ color: '#667eea', fontSize: 28 }} />
              <Typography variant="h6" fontWeight={600}>
                Study Materials
              </Typography>
            </Box>
            <Button 
              variant="contained" 
              startIcon={<DownloadIcon />}
              onClick={handleDownloadAllMaterials}
              sx={{ 
                textTransform: 'none',
                bgcolor: '#667eea',
                '&:hover': { bgcolor: '#5568d3' }
              }}
            >
              Download All Materials
            </Button>
          </Box>

          <Grid container spacing={3}>
            {loading ? (
              <Grid item xs={12}>
                <Typography>Loading materials...</Typography>
              </Grid>
            ) : resources.length === 0 ? (
              <Grid item xs={12}>
                <Typography color="textSecondary">No study materials available</Typography>
              </Grid>
            ) : (
              // Group resources by subject
              Object.entries(
                resources.reduce((acc, resource) => {
                  const key = resource.subject_name || 'General';
                  if (!acc[key]) acc[key] = [];
                  acc[key].push(resource);
                  return acc;
                }, {})
              ).map(([subjectName, materials]) => (
                <Grid item xs={12} md={6} key={subjectName}>
                  <Card sx={{ boxShadow: '0 2px 8px rgba(0,0,0,0.08)', borderRadius: 2, height: '100%' }}>
                    <CardContent>
                      <Box display="flex" alignItems="center" gap={2} mb={2}>
                        <Avatar sx={{ bgcolor: '#667eea', width: 48, height: 48 }}>
                          <MenuBookIcon />
                        </Avatar>
                        <Box>
                          <Typography variant="h6" fontWeight={600}>
                            {subjectName}
                          </Typography>
                          <Typography variant="caption" color="textSecondary">
                            {materials[0]?.subject_code || 'N/A'}
                          </Typography>
                        </Box>
                      </Box>

                      <Divider sx={{ my: 2 }} />

                      <List sx={{ p: 0 }}>
                        {materials.map((material, matIdx) => (
                          <ListItem 
                            key={material._id || matIdx}
                            sx={{ 
                              px: 0,
                              py: 1,
                              '&:hover': { bgcolor: '#f5f5f5', borderRadius: 1 }
                            }}
                          >
                            <Box display="flex" alignItems="center" gap={2} width="100%">
                              {getFileIcon(material.file_type || material.type || 'pdf')}
                              <Box flex={1}>
                                <Typography variant="body2" fontWeight={500}>
                                  {material.title || material.file_name || material.name}
                                </Typography>
                                <Typography variant="caption" color="textSecondary">
                                  {material.size || 'N/A'}
                                </Typography>
                              </Box>
                              <IconButton size="small" color="primary">
                                <DownloadIcon fontSize="small" />
                              </IconButton>
                            </Box>
                          </ListItem>
                        ))}
                      </List>
                    </CardContent>
                  </Card>
                </Grid>
              ))
            )}
          </Grid>
        </Box>
      )}

      {/* Assignments Section */}
      {activeTab === 2 && (
        <Box>
          <Box display="flex" alignItems="center" gap={1} mb={3}>
            <AssignmentIcon sx={{ color: '#667eea', fontSize: 28 }} />
            <Typography variant="h6" fontWeight={600}>
              Assignments
            </Typography>
          </Box>

          <Grid container spacing={3}>
            {loading ? (
              <Grid item xs={12}>
                <Typography>Loading assignments...</Typography>
              </Grid>
            ) : assignments.length === 0 ? (
              <Grid item xs={12}>
                <Typography color="textSecondary">No assignments available</Typography>
              </Grid>
            ) : (
              assignments.map((assignment) => {
                // Calculate status based on submission and deadline
                const submission = submissions.find(s => s.assignment_id?._id === assignment._id || s.assignment_id === assignment._id);
                const isOverdue = new Date() > new Date(assignment.deadline);
                const status = submission ? 'submitted' : isOverdue ? 'overdue' : 'pending';
                
                return (
                  <Grid item xs={12} md={6} key={assignment._id || assignment.id}>
                    <Card 
                      sx={{ 
                        boxShadow: '0 2px 8px rgba(0,0,0,0.08)', 
                        borderRadius: 2,
                        transition: 'all 0.2s',
                        '&:hover': {
                          boxShadow: '0 4px 16px rgba(0,0,0,0.12)'
                        }
                      }}
                    >
                      <CardContent>
                        <Box display="flex" justifyContent="space-between" alignItems="start" mb={2}>
                          <Box>
                            <Typography variant="h6" fontWeight={600} mb={0.5}>
                              {assignment.title}
                            </Typography>
                            <Chip 
                              label={assignment.subject_code || 'N/A'} 
                              size="small" 
                              sx={{ 
                                bgcolor: '#e3e8ff', 
                                color: '#667eea',
                                fontWeight: 600,
                                fontSize: '0.75rem'
                              }}
                            />
                          </Box>
                          {getStatusChip(status)}
                        </Box>

                        <Typography variant="body2" color="textSecondary" mb={2}>
                          {assignment.description || 'No description provided'}
                        </Typography>

                        <Box 
                          sx={{ 
                            bgcolor: '#f8f9ff', 
                            p: 1.5, 
                            borderRadius: 1,
                            mb: 2
                          }}
                        >
                          <Typography variant="caption" color="textSecondary">
                            Due Date
                          </Typography>
                          <Typography variant="body2" fontWeight={600} color={isOverdue ? 'error.main' : 'text.primary'}>
                            {new Date(assignment.deadline).toLocaleDateString('en-US', { 
                              month: 'long', 
                              day: 'numeric', 
                              year: 'numeric' 
                            })}
                          </Typography>
                        </Box>

                        {status !== 'submitted' && (
                          <Button 
                            variant="contained" 
                            fullWidth
                            startIcon={<UploadFileIcon />}
                            onClick={() => handleSubmitAssignment(assignment)}
                            sx={{ 
                              textTransform: 'none',
                              bgcolor: '#667eea',
                              '&:hover': { bgcolor: '#5568d3' }
                            }}
                          >
                            Upload Submission
                          </Button>
                        )}
                      </CardContent>
                    </Card>
                  </Grid>
                );
              })
            )}
          </Grid>
        </Box>
      )}

      {/* Submit Assignment Dialog */}
      <Dialog 
        open={submitDialogOpen} 
        onClose={() => setSubmitDialogOpen(false)}
        maxWidth="sm"
        fullWidth
      >
        <DialogTitle>
          <Typography variant="h6" fontWeight={600}>
            Submit Assignment
          </Typography>
          <Typography variant="body2" color="textSecondary">
            {selectedAssignment?.topic}
          </Typography>
        </DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 2 }}>
            <Button
              variant="outlined"
              component="label"
              fullWidth
              sx={{ 
                height: 120,
                borderStyle: 'dashed',
                textTransform: 'none',
                mb: 2
              }}
            >
              <Box textAlign="center">
                <UploadFileIcon sx={{ fontSize: 40, color: '#667eea', mb: 1 }} />
                <Typography variant="body2">
                  Click to upload file
                </Typography>
                <Typography variant="caption" color="textSecondary">
                  Supports: PDF, DOC, DOCX, JPG, PNG, ZIP
                </Typography>
              </Box>
              <input
                type="file"
                hidden
                accept=".pdf,.doc,.docx,.jpg,.jpeg,.png,.zip"
                onChange={handleFileUpload}
              />
            </Button>
            
            {uploadFile && (
              <Paper sx={{ p: 2, bgcolor: '#f8f9ff' }}>
                <Typography variant="body2" fontWeight={500}>
                  Selected: {uploadFile.name}
                </Typography>
                <Typography variant="caption" color="textSecondary">
                  Size: {(uploadFile.size / 1024 / 1024).toFixed(2)} MB
                </Typography>
              </Paper>
            )}
          </Box>
        </DialogContent>
        <DialogActions sx={{ p: 3, pt: 0 }}>
          <Button onClick={() => setSubmitDialogOpen(false)} sx={{ textTransform: 'none' }}>
            Cancel
          </Button>
          <Button 
            variant="contained" 
            onClick={() => {
              alert('Assignment submitted successfully!');
              setSubmitDialogOpen(false);
              setUploadFile(null);
            }}
            disabled={!uploadFile}
            sx={{ 
              textTransform: 'none',
              bgcolor: '#667eea',
              '&:hover': { bgcolor: '#5568d3' }
            }}
          >
            Submit Assignment
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Academics;
