import React, { useContext } from 'react';
import { Box, Typography, Grid, Card, CardContent, Fade, Button, LinearProgress, Alert } from '@mui/material';
import { UserContext } from '../App';

const mockAnalytics = [
  { id: 1, title: 'Post Engagement', value: 85, description: 'Engagement rate for announcements.' },
  { id: 2, title: 'Assignment Submissions', value: 92, description: 'Percentage of students submitted assignments.' },
];

const Analytics = () => {
  const { user } = useContext(UserContext);
  
  // Only HOD and DEAN can access analytics
  const hasAccess = user && ['HOD', 'DEAN'].includes(user.role);
  
  if (!hasAccess) {
    return (
      <Box>
        <Alert severity="error">
          Access Denied: Only HOD and DEAN can view analytics.
        </Alert>
      </Box>
    );
  }
  
  return (
    <Box>
      <Typography variant="h4" fontWeight={700} gutterBottom>Analytics & Reporting</Typography>
      <Grid container spacing={3}>
        {mockAnalytics.map(a => (
          <Grid item xs={12} md={6} key={a.id}>
            <Fade in timeout={700}>
              <Card elevation={3} sx={{ borderRadius: 3 }}>
                <CardContent>
                  <Typography variant="h6">{a.title}</Typography>
                  <Typography variant="body2" sx={{ mb: 2 }}>{a.description}</Typography>
                  <LinearProgress variant="determinate" value={a.value} sx={{ height: 8, borderRadius: 5 }} />
                  <Typography variant="body2" sx={{ mt: 1 }}>{a.value}%</Typography>
                  <Box mt={2}>
                    <Button variant="contained" color="primary">Export Report</Button>
                  </Box>
                </CardContent>
              </Card>
            </Fade>
          </Grid>
        ))}
      </Grid>
      {/* TODO: Add more analytics and export options */}
    </Box>
  );
};

export default Analytics;
