import React, { useState, useContext } from 'react';
import { Box, Typography, Grid, Card, CardContent, Button, Fade, Chip, TextField, MenuItem, Dialog, DialogTitle, DialogContent, DialogActions, FormControl, InputLabel, Select } from '@mui/material';
import PreviewIcon from '@mui/icons-material/Preview';
import DownloadIcon from '@mui/icons-material/Download';
import { UserContext } from '../App';
import { broadcastContentNotification } from '../utils/notificationBroadcast';

const mockResources = [
  { id: 1, name: 'Lecture Notes.pdf', type: 'PDF', version: 'v2.1', folder: 'DBMS', subjectName: 'Database Management Systems', subjectCode: 'CSE401', preview: true, downloads: 120, uploaded_by: 1, expiryDate: '2025-12-31', status: 'approved' },
  { id: 2, name: 'Assignment1.docx', type: 'DOCX', version: 'v1.0', folder: 'AI', subjectName: 'Artificial Intelligence', subjectCode: 'CSE502', preview: false, downloads: 85, uploaded_by: 2, expiryDate: '2025-12-15', status: 'approved' },
];

const types = ['All', 'PDF', 'DOCX', 'Link', 'Google Form', 'Google Sheet'];
const folders = ['All', 'DBMS', 'AI', 'ML', 'OS', 'CN'];

const Resources = () => {
  const { user } = useContext(UserContext);
  const [type, setType] = useState('All');
  const [folder, setFolder] = useState('All');
  const [sortBy, setSortBy] = useState('date');
  const [openUploadDialog, setOpenUploadDialog] = useState(false);
  const [openPreviewDialog, setOpenPreviewDialog] = useState(false);
  const [selectedResource, setSelectedResource] = useState(null);
  const [newResource, setNewResource] = useState({
    name: '',
    type: 'PDF',
    subjectName: '',
    subjectCode: '',
    folder: 'DBMS',
    link: '',
    expiryDate: '',
    file: null
  });
  
  // Permissions - Faculty/HOD/DEAN can upload and manage resources
  const canUpload = user && ['HOD', 'DEAN', 'Faculty'].includes(user?.role);
  const canEdit = (resource) => {
    if (!user) return false;
    if (['HOD', 'DEAN'].includes(user.role)) return true;
    if (user.role === 'Faculty') return resource.uploaded_by === user.id;
    return false;
  };
  const canDelete = (resource) => {
    if (!user) return false;
    if (['HOD', 'DEAN'].includes(user.role)) return true;
    if (user.role === 'Faculty') return resource.uploaded_by === user.id;
    return false;
  };
  
  const handleUploadResource = () => {
    console.log('Uploading resource:', newResource);
    
    // Broadcast notification to students
    broadcastContentNotification({
      type: 'Resource',
      title: `New Resource: ${newResource.name}`,
      message: `${newResource.subjectName} (${newResource.subjectCode}) - ${newResource.folder}`,
      contentId: Date.now(), // Replace with actual ID from API response
      priority: 'low'
    });
    
    // TODO: API call to upload resource
    setOpenUploadDialog(false);
    setNewResource({
      name: '',
      type: 'PDF',
      subjectName: '',
      subjectCode: '',
      folder: 'DBMS',
      link: '',
      expiryDate: '',
      file: null
    });
  };
  
  const handleDeleteResource = (id) => {
    console.log('Deleting resource:', id);
    // TODO: API call to delete resource
  };

  const handlePreview = (resource) => {
    setSelectedResource(resource);
    setOpenPreviewDialog(true);
  };

  const handleDownload = (resource) => {
    // Simulate download
    console.log('Downloading:', resource.name);
    // In a real app, this would trigger actual file download
    alert(`Downloading ${resource.name}...`);
  };

  const filtered = mockResources.filter(r => {
    const matchesType = type === 'All' || r.type === type;
    const matchesFolder = folder === 'All' || r.folder === folder;
    
    // Students only see approved resources
    if (user?.role === 'Student') {
      return matchesType && matchesFolder && r.status === 'approved';
    }
    
    // Faculty see their own resources and approved resources
    if (user?.role === 'Faculty') {
      return matchesType && matchesFolder && (r.uploaded_by === user?.id || r.status === 'approved');
    }
    
    // HOD and DEAN see all resources
    return matchesType && matchesFolder;
  });
  
  const sortedResources = React.useMemo(() => {
    let sorted = [...filtered];
    switch(sortBy) {
      case 'date':
        sorted.sort((a, b) => b.id - a.id); // Assuming higher ID = newer
        break;
      case 'expiry':
        sorted.sort((a, b) => new Date(a.expiryDate) - new Date(b.expiryDate));
        break;
      case 'downloads':
        sorted.sort((a, b) => b.downloads - a.downloads);
        break;
      case 'subject':
        sorted.sort((a, b) => (a.subjectCode || '').localeCompare(b.subjectCode || ''));
        break;
      default:
        break;
    }
    return sorted;
  }, [filtered, sortBy]);
  
  return (
    <Box>
      <Typography variant="h4" fontWeight={700} gutterBottom>Resources</Typography>
      <Box display="flex" gap={2} mb={3} flexWrap="wrap">
        <TextField select label="Type" value={type} onChange={e => setType(e.target.value)} sx={{ minWidth: 150 }}>
          {types.map(t => <MenuItem key={t} value={t}>{t}</MenuItem>)}
        </TextField>
        <TextField select label="Folder" value={folder} onChange={e => setFolder(e.target.value)} sx={{ minWidth: 150 }}>
          {folders.map(f => <MenuItem key={f} value={f}>{f}</MenuItem>)}
        </TextField>
        {canUpload && (
          <Button variant="contained" color="primary" onClick={() => setOpenUploadDialog(true)}>
            Upload Resource
          </Button>
        )}
      </Box>
      <Grid container spacing={3}>
        {sortedResources.map(r => (
          <Grid item xs={12} md={6} key={r.id}>
            <Fade in timeout={700}>
              <Card elevation={3} sx={{ borderRadius: 3 }}>
                <CardContent>
                  <Typography variant="h6">{r.name}</Typography>
                  <Chip label={r.type} color="info" sx={{ mr: 1 }} />
                  <Chip label={`Version: ${r.version}`} color="primary" sx={{ mr: 1 }} />
                  <Chip label={`Folder: ${r.folder}`} color="secondary" sx={{ mr: 1 }} />
                  <Chip label={`Downloads: ${r.downloads}`} color="success" sx={{ mr: 1 }} />
                  <Box mt={2}>
                    <Button 
                      variant="contained" 
                      color="primary" 
                      sx={{ mr: 1 }}
                      startIcon={<PreviewIcon />}
                      onClick={() => handlePreview(r)}
                      disabled={!r.preview}
                    >
                      Preview
                    </Button>
                    <Button 
                      variant="outlined" 
                      color="secondary"
                      startIcon={<DownloadIcon />}
                      onClick={() => handleDownload(r)}
                    >
                      Download
                    </Button>
                  </Box>
                </CardContent>
              </Card>
            </Fade>
          </Grid>
        ))}
      </Grid>

      {/* Preview Dialog */}
      <Dialog 
        open={openPreviewDialog} 
        onClose={() => setOpenPreviewDialog(false)}
        maxWidth="lg"
        fullWidth
      >
        <DialogTitle>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Typography variant="h6">{selectedResource?.name}</Typography>
            <Box>
              <Chip label={selectedResource?.type} color="primary" size="small" sx={{ mr: 1 }} />
              <Chip label={selectedResource?.version} color="info" size="small" />
            </Box>
          </Box>
        </DialogTitle>
        <DialogContent dividers>
          {selectedResource && (
            <Box>
              {/* PDF Preview */}
              {selectedResource.type === 'PDF' && (
                <Box sx={{ 
                  width: '100%', 
                  height: '70vh', 
                  display: 'flex', 
                  justifyContent: 'center', 
                  alignItems: 'center',
                  bgcolor: 'grey.100',
                  borderRadius: 1
                }}>
                  <Typography variant="body1" color="text.secondary">
                    PDF Preview would display here. In production, use an iframe or PDF viewer library like react-pdf.
                  </Typography>
                  {/* Example: <iframe src={selectedResource.url} width="100%" height="100%" /> */}
                </Box>
              )}
              
              {/* Image Preview */}
              {['JPG', 'JPEG', 'PNG', 'GIF'].includes(selectedResource.type?.toUpperCase()) && (
                <Box sx={{ textAlign: 'center' }}>
                  <img 
                    src={selectedResource.url || '/placeholder-image.jpg'} 
                    alt={selectedResource.name}
                    style={{ maxWidth: '100%', maxHeight: '70vh', objectFit: 'contain' }}
                  />
                </Box>
              )}
              
              {/* Document Info for non-previewable types */}
              {!['PDF', 'JPG', 'JPEG', 'PNG', 'GIF'].includes(selectedResource.type?.toUpperCase()) && (
                <Box sx={{ p: 3, textAlign: 'center' }}>
                  <Typography variant="h6" gutterBottom>Preview not available</Typography>
                  <Typography variant="body2" color="text.secondary" gutterBottom>
                    {selectedResource.type} files cannot be previewed directly.
                  </Typography>
                  <Button 
                    variant="contained" 
                    startIcon={<DownloadIcon />}
                    onClick={() => handleDownload(selectedResource)}
                    sx={{ mt: 2 }}
                  >
                    Download to View
                  </Button>
                </Box>
              )}
              
              {/* Resource Details */}
              <Box sx={{ mt: 3, p: 2, bgcolor: 'grey.50', borderRadius: 1 }}>
                <Grid container spacing={2}>
                  <Grid item xs={6}>
                    <Typography variant="caption" color="text.secondary">Subject</Typography>
                    <Typography variant="body2">{selectedResource.subjectName} ({selectedResource.subjectCode})</Typography>
                  </Grid>
                  <Grid item xs={6}>
                    <Typography variant="caption" color="text.secondary">Folder</Typography>
                    <Typography variant="body2">{selectedResource.folder}</Typography>
                  </Grid>
                  <Grid item xs={6}>
                    <Typography variant="caption" color="text.secondary">Uploaded By</Typography>
                    <Typography variant="body2">{selectedResource.uploaded_by}</Typography>
                  </Grid>
                  <Grid item xs={6}>
                    <Typography variant="caption" color="text.secondary">Downloads</Typography>
                    <Typography variant="body2">{selectedResource.downloads}</Typography>
                  </Grid>
                  <Grid item xs={12}>
                    <Typography variant="caption" color="text.secondary">Expires On</Typography>
                    <Typography variant="body2">{selectedResource.expiryDate}</Typography>
                  </Grid>
                </Grid>
              </Box>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button 
            startIcon={<DownloadIcon />}
            variant="contained"
            onClick={() => handleDownload(selectedResource)}
          >
            Download
          </Button>
          <Button onClick={() => setOpenPreviewDialog(false)}>Close</Button>
        </DialogActions>
      </Dialog>

      {/* TODO: Add folder structure, version control, and material previews */}
    </Box>
  );
};

export default Resources;
