import React, { useState, useContext } from 'react';
import { 
  Box, Typography, Grid, Card, CardContent, Button, TextField, MenuItem, 
  Chip, Dialog, DialogTitle, DialogContent, DialogActions, Paper, List,
  ListItem, ListItemText, Divider, IconButton, InputAdornment
} from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import { UserContext } from '../App';
import { broadcastContentNotification } from '../utils/notificationBroadcast';

const mockForms = [
  { 
    id: 1, 
    title: 'Course Feedback Form', 
    category: 'Feedback', 
    subject: 'Database Management Systems',
    subjectCode: 'CSE401',
    googleFormLink: 'https://forms.google.com/example1',
    googleSheetLink: 'https://docs.google.com/spreadsheets/example1',
    responses: 45,
    createdDate: '2025-11-20',
    expiryDate: '2025-12-20',
    created_by: 1,
    createdBy: 'Faculty',
    status: 'approved'
  },
  { 
    id: 2, 
    title: 'Event Registration', 
    category: 'Registration', 
    subject: 'Artificial Intelligence',
    subjectCode: 'CSE502',
    googleFormLink: 'https://forms.google.com/example2',
    googleSheetLink: 'https://docs.google.com/spreadsheets/example2',
    responses: 120,
    createdDate: '2025-11-25',
    expiryDate: '2025-12-05',
    created_by: 2,
    createdBy: 'HOD',
    status: 'approved'
  },
  { 
    id: 3, 
    title: 'Lab Feedback', 
    category: 'Feedback', 
    subject: 'Operating Systems',
    subjectCode: 'CSE403',
    googleFormLink: 'https://forms.google.com/example3',
    googleSheetLink: 'https://docs.google.com/spreadsheets/example3',
    responses: 32,
    createdDate: '2025-11-15',
    expiryDate: '2025-12-15',
    created_by: 3,
    createdBy: 'Faculty',
    status: 'approved'
  }
];

const categories = ['All', 'Feedback', 'Registration', 'Survey', 'Assessment'];
const subjects = ['All', 'CSE401', 'CSE502', 'CSE403'];

const Forms = () => {
  const { user } = useContext(UserContext);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('All');
  const [subject, setSubject] = useState('All');
  const [sortBy, setSortBy] = useState('createdDate');
  const [forms, setForms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [openCreateDialog, setOpenCreateDialog] = useState(false);
  const [openViewDialog, setOpenViewDialog] = useState(false);
  const [selectedForm, setSelectedForm] = useState(null);
  const [newForm, setNewForm] = useState({
    title: '',
    category: 'Feedback',
    subject: '',
    subjectCode: '',
    googleFormLink: '',
    googleSheetLink: '',
    expiryDate: ''
  });

  // Fetch forms from database
  React.useEffect(() => {
    const token = localStorage.getItem('token');
    
    fetch('/api/forms', {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.ok ? res.json() : [])
      .then(data => {
        setForms(data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching forms:', err);
        setLoading(false);
      });
  }, []);

  const canCreate = user && ['HOD', 'DEAN', 'Faculty'].includes(user?.role);
  const canEdit = (form) => {
    if (!user) return false;
    if (['HOD', 'DEAN'].includes(user.role)) return true;
    if (user.role === 'Faculty') return form.created_by === user.id;
    return false;
  };
  const canDelete = (form) => {
    if (!user) return false;
    if (['HOD', 'DEAN'].includes(user.role)) return true;
    if (user.role === 'Faculty') return form.created_by === user.id;
    return false;
  };

  // Filter forms
  const filtered = forms.filter(f => {
    const matchesSearch = f.title.toLowerCase().includes(search.toLowerCase()) || 
                         f.subject.toLowerCase().includes(search.toLowerCase());
    const matchesCategory = category === 'All' || f.category === category;
    const matchesSubject = subject === 'All' || f.subjectCode === subject;
    
    // Students only see approved forms
    if (user?.role === 'Student') {
      return matchesSearch && matchesCategory && matchesSubject && f.status === 'approved';
    }
    
    // Faculty see their own forms and approved forms
    if (user?.role === 'Faculty') {
      return matchesSearch && matchesCategory && matchesSubject && (f.created_by === user?.id || f.status === 'approved');
    }
    
    // HOD and DEAN see all forms
    return matchesSearch && matchesCategory && matchesSubject;
  });

  // Sort forms
  const sorted = React.useMemo(() => {
    let result = [...filtered];
    switch(sortBy) {
      case 'category':
        result.sort((a, b) => a.category.localeCompare(b.category));
        break;
      case 'subject':
        result.sort((a, b) => a.subjectCode.localeCompare(b.subjectCode));
        break;
      case 'createdDate':
        result.sort((a, b) => new Date(b.createdDate) - new Date(a.createdDate));
        break;
      case 'expiryDate':
        result.sort((a, b) => new Date(a.expiryDate) - new Date(b.expiryDate));
        break;
      case 'responses':
        result.sort((a, b) => b.responses - a.responses);
        break;
      default:
        break;
    }
    return result;
  }, [filtered, sortBy]);

  const handleCreateForm = () => {
    const form = {
      id: Date.now(),
      ...newForm,
      responses: 0,
      createdDate: new Date().toISOString().split('T')[0],
      createdBy: user?.role || 'Faculty'
    };
    setForms([form, ...forms]);
    
    // Broadcast notification to students
    broadcastContentNotification({
      type: 'Form',
      title: newForm.title,
      message: `New ${newForm.category} form: ${newForm.subject}`,
      contentId: form.id,
      priority: 'medium'
    });
    
    setOpenCreateDialog(false);
    setNewForm({
      title: '',
      category: 'Feedback',
      subject: '',
      subjectCode: '',
      googleFormLink: '',
      googleSheetLink: '',
      expiryDate: ''
    });
  };

  const handleDeleteForm = (id) => {
    if (window.confirm('Are you sure you want to delete this form?')) {
      setForms(forms.filter(f => f.id !== id));
    }
  };

  return (
    <Box>
      <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
        <Typography variant="h4" fontWeight={700}>Forms & Links</Typography>
        {canCreate && (
          <Button 
            variant="contained" 
            color="primary" 
            startIcon={<AddIcon />}
            onClick={() => setOpenCreateDialog(true)}
          >
            Create Form Link
          </Button>
        )}
      </Box>

      {/* Filters and Search */}
      <Box display="flex" gap={2} mb={3} flexWrap="wrap">
        <TextField 
          label="Search Forms" 
          variant="outlined" 
          value={search} 
          onChange={e => setSearch(e.target.value)} 
          sx={{ flex: 1, minWidth: 250 }}
          InputProps={{
            startAdornment: (
              <InputAdornment position="start">
                <SearchIcon />
              </InputAdornment>
            ),
          }}
        />
        <TextField 
          select 
          label="Category" 
          value={category} 
          onChange={e => setCategory(e.target.value)} 
          sx={{ minWidth: 150 }}
        >
          {categories.map(cat => <MenuItem key={cat} value={cat}>{cat}</MenuItem>)}
        </TextField>
        <TextField 
          select 
          label="Subject" 
          value={subject} 
          onChange={e => setSubject(e.target.value)} 
          sx={{ minWidth: 150 }}
        >
          {subjects.map(sub => <MenuItem key={sub} value={sub}>{sub}</MenuItem>)}
        </TextField>
        <TextField 
          select 
          label="Sort By" 
          value={sortBy} 
          onChange={e => setSortBy(e.target.value)} 
          sx={{ minWidth: 150 }}
        >
          <MenuItem value="createdDate">Created Date (Newest)</MenuItem>
          <MenuItem value="expiryDate">Expiry Date</MenuItem>
          <MenuItem value="category">Category</MenuItem>
          <MenuItem value="subject">Subject Code</MenuItem>
          <MenuItem value="responses">Response Count</MenuItem>
        </TextField>
      </Box>

      {/* Forms Grid */}
      <Grid container spacing={3}>
        {sorted.map(form => (
          <Grid item xs={12} md={6} key={form.id}>
            <Card elevation={3} sx={{ borderRadius: 3, height: '100%' }}>
              <CardContent>
                <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                  <Typography variant="h6" fontWeight={600}>{form.title}</Typography>
                  {canDelete(form) && (
                    <IconButton 
                      size="small" 
                      color="error"
                      onClick={() => handleDeleteForm(form.id)}
                    >
                      <DeleteIcon fontSize="small" />
                    </IconButton>
                  )}
                </Box>
                
                <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                  <Chip label={form.category} color="primary" size="small" />
                  <Chip label={form.subjectCode} color="info" size="small" />
                  <Chip label={`${form.responses} responses`} color="success" size="small" />
                </Box>

                <Typography variant="body2" color="text.secondary" gutterBottom>
                  {form.subject}
                </Typography>
                <Typography variant="caption" color="text.secondary" display="block" gutterBottom>
                  Created: {form.createdDate} â€¢ Expires: {form.expiryDate}
                </Typography>
                <Typography variant="caption" color="text.secondary" display="block" sx={{ mb: 2 }}>
                  By: {form.createdBy}
                </Typography>

                <Box display="flex" gap={1} flexWrap="wrap">
                  <Button 
                    variant="contained" 
                    color="primary"
                    startIcon={<OpenInNewIcon />}
                    href={form.googleFormLink}
                    target="_blank"
                    size="small"
                  >
                    Open Form
                  </Button>
                  {form.googleSheetLink && (
                    <Button 
                      variant="outlined" 
                      color="secondary"
                      startIcon={<OpenInNewIcon />}
                      href={form.googleSheetLink}
                      target="_blank"
                      size="small"
                    >
                      View Responses
                    </Button>
                  )}
                  <Button 
                    variant="text" 
                    size="small"
                    onClick={() => {
                      setSelectedForm(form);
                      setOpenViewDialog(true);
                    }}
                  >
                    Details
                  </Button>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {sorted.length === 0 && (
        <Box sx={{ textAlign: 'center', py: 8 }}>
          <Typography variant="h6" color="text.secondary">
            No forms found
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {canCreate ? 'Create a new form link to get started' : 'Check back later for new forms'}
          </Typography>
        </Box>
      )}

      {/* Create Form Dialog */}
      <Dialog open={openCreateDialog} onClose={() => setOpenCreateDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Create Form Link</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Form Title"
            value={newForm.title}
            onChange={(e) => setNewForm({ ...newForm, title: e.target.value })}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            select
            label="Category"
            value={newForm.category}
            onChange={(e) => setNewForm({ ...newForm, category: e.target.value })}
            margin="normal"
          >
            {categories.filter(c => c !== 'All').map(cat => (
              <MenuItem key={cat} value={cat}>{cat}</MenuItem>
            ))}
          </TextField>
          <TextField
            fullWidth
            label="Subject Name"
            value={newForm.subject}
            onChange={(e) => setNewForm({ ...newForm, subject: e.target.value })}
            margin="normal"
            required
          />
          <TextField
            fullWidth
            label="Subject Code"
            value={newForm.subjectCode}
            onChange={(e) => setNewForm({ ...newForm, subjectCode: e.target.value })}
            margin="normal"
            placeholder="e.g., CSE401"
            required
          />
          <TextField
            fullWidth
            label="Google Form Link"
            value={newForm.googleFormLink}
            onChange={(e) => setNewForm({ ...newForm, googleFormLink: e.target.value })}
            margin="normal"
            placeholder="https://forms.google.com/..."
            required
          />
          <TextField
            fullWidth
            label="Google Sheet Link (Responses)"
            value={newForm.googleSheetLink}
            onChange={(e) => setNewForm({ ...newForm, googleSheetLink: e.target.value })}
            margin="normal"
            placeholder="https://docs.google.com/spreadsheets/..."
            helperText="Optional - Link to view form responses"
          />
          <TextField
            fullWidth
            label="Expiry Date"
            type="date"
            value={newForm.expiryDate}
            onChange={(e) => setNewForm({ ...newForm, expiryDate: e.target.value })}
            margin="normal"
            InputLabelProps={{ shrink: true }}
            required
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenCreateDialog(false)}>Cancel</Button>
          <Button 
            onClick={handleCreateForm} 
            variant="contained"
            disabled={!newForm.title || !newForm.googleFormLink || !newForm.subjectCode || !newForm.expiryDate}
          >
            Create
          </Button>
        </DialogActions>
      </Dialog>

      {/* View Form Details Dialog */}
      <Dialog open={openViewDialog} onClose={() => setOpenViewDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>{selectedForm?.title}</DialogTitle>
        <DialogContent dividers>
          {selectedForm && (
            <Box>
              <Box sx={{ mb: 3 }}>
                <Typography variant="subtitle2" color="text.secondary" gutterBottom>Category & Subject</Typography>
                <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                  <Chip label={selectedForm.category} color="primary" />
                  <Chip label={selectedForm.subjectCode} color="info" />
                </Box>
                <Typography variant="body2">{selectedForm.subject}</Typography>
              </Box>

              <Box sx={{ mb: 3 }}>
                <Typography variant="subtitle2" color="text.secondary" gutterBottom>Statistics</Typography>
                <Typography variant="body2">Total Responses: {selectedForm.responses}</Typography>
                <Typography variant="body2">Created By: {selectedForm.createdBy}</Typography>
              </Box>

              <Box sx={{ mb: 3 }}>
                <Typography variant="subtitle2" color="text.secondary" gutterBottom>Dates</Typography>
                <Typography variant="body2">Created: {selectedForm.createdDate}</Typography>
                <Typography variant="body2">Expires: {selectedForm.expiryDate}</Typography>
              </Box>

              <Box>
                <Typography variant="subtitle2" color="text.secondary" gutterBottom>Links</Typography>
                <Button 
                  variant="outlined" 
                  fullWidth
                  startIcon={<OpenInNewIcon />}
                  href={selectedForm.googleFormLink}
                  target="_blank"
                  sx={{ mb: 1 }}
                >
                  Open Google Form
                </Button>
                {selectedForm.googleSheetLink && (
                  <Button 
                    variant="outlined" 
                    fullWidth
                    startIcon={<OpenInNewIcon />}
                    href={selectedForm.googleSheetLink}
                    target="_blank"
                  >
                    View Responses Sheet
                  </Button>
                )}
              </Box>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenViewDialog(false)}>Close</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Forms;
